import './App.css';
import { Fragment, useState } from 'react';
import AddTask from './Users/AddTask';
import UserList from './Users/UserList';
function App() {
  const [taskList,setTaskList] = useState(['']);
  const addTaskHandler =(mtask) => {
    setTaskList((prevTask) => {
      return [
        ...prevTask,
        {task:mtask,id: Math.random().toString()}
      ]
    })
  };
  const removeItem = props => {
    this.setState({taskList:this.state.taskList.splice(props,1)})
    console.log(this.state.taskList)
}

  return (

    <Fragment>
      <AddTask onAddTask={addTaskHandler} ></AddTask>
      <UserList users={taskList}></UserList>
      <button onClick={() => this.removeItem()}>X</button>
    </Fragment>
  );
}

export default App;
