import './App.css';
import { Fragment, useState } from 'react';
import AddTask from './Users/AddTask';
import UserList from './Users/UserList';
function App() {
  const [taskList,setTaskList] = React.useState([
    {
      text: "Learn about React",
      id: 'e1'
    },
    {
      text: "Meet friend for lunch",
      id: 'e2'
    },
    {
      text: "Build really cool todo app",
      id: 'e3'
    }
  ]);
  const addTaskHandler =(mtask) => {
    setTaskList((prevTask) => {
      return [
        ...prevTask,
        {task:mtask,id:Math.random().toString()}
      ]
    })
  };
  // const deleteTaskHandler = (id) => {
  //   const arrayDelete= taskList.filter(delete1 => delete1.id === id);
  //   setTaskList(arrayDelete);
  // }
  const deleteTaskHandler = (id) => {
    const newToDo = [...taskList]
    newToDo.splice(id, 1);
    setTaskList(newToDo)
    // const arrayDelete= taskList.filter(delete1 => delete1.id === id);
    // setTaskList(arrayDelete);
  }




  return (

    <Fragment>
      <AddTask onAddTask={addTaskHandler}  ></AddTask>
      <UserList users={taskList} onDeleteTask ={deleteTaskHandler}></UserList>
    </Fragment>
  );
}

export default App;
