import React ,{useState} from 'react'
import Card from '../UI/Card';
import classes from './YsersList.module.css'
import Button from '../UI/Button';


const UserList = (props) => {
    const [isEditing, setIsEditing] = useState(false);
    const [taskList,setTaskList] = useState([]);

    const startEditingHandler = () => {
        setIsEditing(true);
    };

      const stopEditingHandler = () => {
        setIsEditing(false);
    };

    const saveTask = (editTask,id) => {
        console.log(editTask,id)

        let editedTaskList = props.taskList.map((element,index) => {
          if (id === element.id) {
            element.task = editTask;
          }
          return element

        })
        console.log(editedTaskList)
        props.setTaskList(editedTaskList);
        props.edited1({task:""})
    }
    const editChangeHandler = (event) => {
        props.edited1({task:event.target.value, id:props.edited.id});

    };


    return(
        <Card className={classes.users} >
            <ul>
                {props.users.map((user,index) => (
                    <li key={user.id} className={classes.text}>
                        {user.task}



                        <div>
                            {!isEditing && (
                                <Button onClick={() => {props.onUpdateTask (index) ; startEditingHandler()} }>Edit task</Button>
                            )}

                            {isEditing && (
                                <div onCancel={stopEditingHandler}> 
                                    <input id='taskEdit' placeholder="Edit task?"  type ='text' value ={props.edited.task} 
                                    onChange={editChangeHandler} className={classes.form__input} ></input>
                                    <Button className={classes.button} onClick={()=> {
                                    saveTask(props.edited.task,props.edited.id) 
                                    }}>Save Task</Button>
                                </div>
                            )}
                            <Button onClick={() => {props.onDeleteTask (index)} }>Done task</Button>
                            {/* <Button onClick={() => {props.onUpdateTask (index)}}>Edit task</Button> */}

                        </div>

                    </li>
                ))}

            </ul>
        </Card>

    );
}
export default UserList;
