import './App.css';
import { Fragment, useState } from 'react';
import AddTask from './Users/AddTask';
import UserList from './Users/UserList';
function App() {
  const [taskList,setTaskList] = useState(['']);
  const addTaskHandler =(mtask) => {
    setTaskList((prevTask) => {
      return [
        ...prevTask,
        {task:mtask,id:Math.random().toString()}
      ]
    })
  };
  const deleteTaskHandler =(mtask) => {
      return(
        this.setState({taskList: [...this.state.taskList].splice(mtask,1)})
      )

  };


  return (

    <Fragment>
      <AddTask onAddTask={addTaskHandler}  ></AddTask>
      <UserList users={taskList}></UserList>
    </Fragment>
  );
}

export default App;
