import React, { useState } from 'react'
import Error from '../UI/Error';
import Card from '../UI/Card';
import classes from './AddTask.module.css'
import Button from '../UI/Button';
const AddTask = (props) => {
    const [enterTask,setTask] = useState('');
    const [error, setError] = useState();
    const [editedTask,setEditTask] = useState('');

    const addTaskHandler = (event) => {
        event.preventDefault();
        if(enterTask.trim().length === 0 ){
            setError({
                title: 'Invalid input',
                message: 'Please enter a task to do',
            });
            return;
        }
        console.log(enterTask);
        props.onAddTask(enterTask);
        setTask('');


    };

    const taskChangeHandler = (event) => {
        setTask(event.target.value);
    };
    const editChangeHandler = (event) => {
        props.edited1({task:event.target.value, id:props.edited.id});
    };
    const errorHandler = () =>{
        setError(null);
    };

    return(

        <div>
            {error && (
                <Error
                title={error.title}
                message={error.message}
                onConfirm={errorHandler}
                />
            )}
            <Card className={classes.input} >
                <form onSubmit={addTaskHandler}>
                    <label htmlFor="task">Input Task</label>
                    <input id='task' placeholder="What do you need to do?"  type ='text' value ={enterTask} onChange={taskChangeHandler} className='form__input'></input>
                    <Button type='submit'>Add Task</Button>
                    <label htmlFor="taskEdit">Edit Task</label>
                </form>
                <input id='taskEdit' placeholder="Edit task?"  type ='text' value ={props.edited.task} onChange={editChangeHandler}></input>
                <Button  onClick={()=> {
                    props.edited2(props.edited.task,props.edited.id) 
                }}>Save Task</Button>

            </Card>


        </div>

    );
}
export default AddTask;
