import './App.css';
import { Fragment, useState } from 'react';
import AddTask from './Users/AddTask';
import UserList from './Users/UserList';
function App() {
  const [editedList,setEditedList] = useState('')
  const [taskList,setTaskList] = useState([
    {
      task: "Learn about React",
      id: 'e1'
    },
    {
      task: "Meet friend for lunch",
      id: 'e2'
    },
    {
      task: "Build really cool todo app",
      id: 'e3'
    }
  ]);
  const addTaskHandler =(mtask) => {
    setTaskList((prevTask) => {
      return [
        ...prevTask,
        {task:mtask,id:Math.random().toString()}
      ]
    })
    // const newTodos = [...taskList, { task:mtask,id:Math.random().toString() }];
    // setTaskList(newTodos);
  };
  // const deleteTaskHandler = (id) => {
  //   const arrayDelete= taskList.filter(delete1 => delete1.id === id);
  //   console.log(arrayDelete)
  //   setTaskList(arrayDelete);
  // }
  const deleteTaskHandler = (index) => {
    const newToDo = [...taskList];
    newToDo.splice(index,1);
    console.log(newToDo)
    setTaskList(newToDo);
    return newToDo;
  }
  const editTaskHandler = (index) => {
    let newEditItem = taskList.find((ele,index1) => {
      return index1 === index
    });
    console.log(newEditItem);
    setEditedList(newEditItem);
  }
  const saveTask = (editTask,id) => {
    console.log(editTask,id)

    let editedTaskList = taskList.map((element,index) => {
      if (id === element.id) {
        element.task = editTask;
      }
      return element

    })
    console.log(editedTaskList)
    setTaskList(editedTaskList);
  }





  return (


    <Fragment>
      <div class="heading">
        <img class="heading__img" src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/756881/laptop.svg"></img>

        <h1 class="heading__title">To-Do List</h1>
      </div>

      <UserList users={taskList} onDeleteTask ={deleteTaskHandler}  onUpdateTask = {editTaskHandler}></UserList>
      <AddTask onAddTask={addTaskHandler} edited={editedList} edited1={setEditedList}  edited2={saveTask} ></AddTask>
    </Fragment>
  );
}

export default App;
