import React ,{useState} from 'react'
import Card from '../UI/Card';
import classes from './YsersList.module.css'
import Button from '../UI/Button';


const UserList = (props) => {
    const [isEditing, setIsEditing] = useState(false);
    const startEditingHandler = () => {
        setIsEditing(true);
    };

      const stopEditingHandler = () => {
        setIsEditing(false);
    };


    return(
        <Card className={classes.users} >
            <ul>
                {props.users.map((user,index) => (
                    <li key={user.id} className={classes.text}>
                        {user.task}
                        <div>
                        {!isEditing && (
                            <Button onClick={() => {props.onUpdateTask (index) ; startEditingHandler()} }>Edit task</Button>
                        )}

                        {isEditing && (
                            <div onCancel={stopEditingHandler}> 
                                <input id='taskEdit' placeholder="Edit task?"  type ='text' value ={props.edited.task} 
                                onChange={editChangeHandler} className={classes.form__input} onClick={startEditingHandler}></input>
                                <Button className={classes.button} onClick={()=> {
                                saveTask(props.edited.task,props.edited.id) 
                                }}>Save Task</Button>

                            </div>
                    )}
                            <Button onClick={() => {props.onDeleteTask (index)} }>Done task</Button>
                            {/* <Button onClick={() => {props.onUpdateTask (index)}}>Edit task</Button> */}

                        </div>

                    </li>
                ))}

            </ul>
        </Card>

    );
}
export default UserList;
